window.addEventListener("load",(()=>{document.body.classList.remove("preload")})),document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".nav");document.querySelector("#burger-btn").addEventListener("click",(()=>{e.classList.add("nav--open")})),document.querySelector(".nav__overlay").addEventListener("click",(()=>{e.classList.remove("nav--open")}))}));